mapper.update:
update evaluation
set eva_pro_uuid = #{evaProUuid,jdbcType=VARCHAR},
  eva_expert_uuid = #{evaExpertUuid,jdbcType=VARCHAR},
  eva_value = #{evaValue,jdbcType=VARCHAR},
  eva_time = #{evaTime,jdbcType=VARCHAR}
where eva_uuid = #{evaUuid,jdbcType=VARCHAR}

mapper.select:
select * 
from evaluation 
where eva_pro_uuid = #{evaProUuid,jdbcType=VARCHAR}
order by eva_time DESC
limit #{start},#{pagesize}
