mapper.select:
   select e.er_value,o.op_name,i.in_name,i.in_parent,i.in_weight
	    from eva_record as e,eva_option as o,indicator as i
	    where   e.er_option_uuid = o.op_uuid and i.in_uuid = o.in_uuid 
                and e.project_uuid = #{projectUuid,jdbcType=VARCHAR}
                and e.er_in_parent = #{erInParent,jdbcType=VARCHAR}

select *
from eva_record as er
where  er.er_eva_uuid = #{erEvaUuid,jdbcType=VARCHAR} and er.er_in_parent = #{erInParent,jdbcType=VARCHAR}
