mapper.select:
	  select 

	  from pro_indicator


        and pro_projectname LIKE "%"#{proProjectname,jdbcType=VARCHAR}"%"


        and pro_productname LIKE "%"#{proProductname,jdbcType=VARCHAR}"%"


        and pro_background LIKE "%"#{proBackground,jdbcType=VARCHAR}"%"


        and pro_industry LIKE "%"#{proIndustry,jdbcType=VARCHAR}"%"


        and pro_intech >= #{proIntech,jdbcType=DOUBLE}


        and pro_inbus >= #{proInbus,jdbcType=DOUBLE}


        and pro_inviro >= #{proInviro,jdbcType=DOUBLE}


        and pro_prospect >= #{proProspect,jdbcType=DOUBLE}


        and pro_indus >= #{proIndus,jdbcType=DOUBLE}


        and pro_organ >= #{proOrgan,jdbcType=DOUBLE}


        and pro_production >= #{proProduction,jdbcType=DOUBLE}


        and pro_fund between #{proFundMin,jdbcType=DOUBLE} and #{proFundMax,jdbcType=DOUBLE}


        and pro_team >= #{proTeam,jdbcType=DOUBLE}


        and pro_risk between #{proRiskMin,jdbcType=DOUBLE} and #{proRiskMax,jdbcType=DOUBLE}


        and pro_totalvalue >= #{proTotalvalue,jdbcType=DOUBLE}


        limit #{start},#{pagesize}

	  select 

	  from pro_indicator


        and pro_projectname LIKE "%"#{proProjectname,jdbcType=VARCHAR}"%"


        and pro_productname LIKE "%"#{proProductname,jdbcType=VARCHAR}"%"


        and pro_background LIKE "%"#{proBackground,jdbcType=VARCHAR}"%"


        and pro_industry LIKE "%"#{proIndustry,jdbcType=VARCHAR}"%"


        and pro_intech >= #{proIntech,jdbcType=DOUBLE}


        and pro_inbus >= #{proInbus,jdbcType=DOUBLE}


        and pro_inviro >= #{proInviro,jdbcType=DOUBLE}


        and pro_prospect >= #{proProspect,jdbcType=DOUBLE}


        and pro_indus >= #{proIndus,jdbcType=DOUBLE}


        and pro_organ >= #{proOrgan,jdbcType=DOUBLE}


        and pro_production >= #{proProduction,jdbcType=DOUBLE}


        and pro_fund between #{proFundMin,jdbcType=DOUBLE} and #{proFundMax,jdbcType=DOUBLE}


        and pro_team >= #{proTeam,jdbcType=DOUBLE}


        and pro_risk between #{proRiskMin,jdbcType=DOUBLE} and #{proRiskMax,jdbcType=DOUBLE}


        and pro_totalvalue >= #{proTotalvalue,jdbcType=DOUBLE}
